    tags: [
      { name: "Java" },
      { name: "Python" },
      { name: "Go"},
      { name: "Ruby"},
      { name: "Kotlin"},
      { name: "C" },
      { name: "React" },
      { name: "Angular" },
      { name: "R" },
      { name: "C" }
