import React from "react";
import { Row, Col } from "react-bootstrap";
import styles from "./style/style.module.css";

const Rows = ({ obj }) => {
  const status = obj.status;
  let color = "red";
  if(status=="Referred")
  {
    color = "green";
  }
  else if(status=="Feedback")
  {
    color = "yellow";
  }
  else if(status==="Skip")
  {
    color = "red";
  }



  return (
    <Row className={styles.rw}>
      <Col>{obj.name}</Col>
      <Col>{obj.jobid}</Col>
      <Col>{obj.joblink}</Col>

      <Col style={{ color: color }}>{obj.status}</Col>
    </Row>
  );
};

export default Rows;
