import React from "react";
import { Row, Col, Badge } from "react-bootstrap";
import styles from "./style/style.module.css";
import companyLogo from "../../Asset/images/companylogo.png";

const Rows = ({ obj }) => {
  const skills = obj.skill;
  return (
    <Row className={styles.rw}>
      <Col>
        <Col>
          <img className={styles.img} src={companyLogo} alt="CompanyLogo" />

        </Col>
        <Col>{obj.company.name}</Col>
      </Col>
      <Col>{obj.role}</Col>
      <Col>{obj.location}</Col>
      <Col>{obj.experience}</Col>
      <Col>
        {skills.map((item) => (
          <Badge pill bg="primary">
            {item}
          </Badge>
        ))}
      </Col>
    </Row>
  );
};

export default Rows;
